mapper.insert:
INSERT INTO user (user_id, user_password, user_nickname)
VALUES
(#{userId}, #{userPassword}, #{userNickname});

mapper.select:
SELECT user_id as userId, mission_subject as subject, mission_condition1 as condition1, mission_condition2 as condition2, mission_condition3 as condition3
FROM user
WHERE user_id = #{userId}

mapper.update:
UPDATE user
SET mission_subject = #{subject},
mission_condition1 = #{condition1},
mission_condition2 = #{condition2},
mission_condition3 = #{condition3}
WHERE user_id = #{userId}

mapper.select:
SELECT fortune_check 
FROM user
WHERE user_id = #{userId}

mapper.update:
UPDATE user
SET fortune_check = true
WHERE user_id = #{userId}

UPDATE user
SET user_password = #{newPassword}
WHERE user_id = #{userId}
